@Composable
fun RecipeDetail(recipeId: String, store: RecipeDetailsStoreWrapper) {
 val recipe by remember { store.state }.collectAsState(initial = Recipe())

 LaunchedEffect(Unit) {
 store.dispatch(RecipeInfoAction.GetRecipeInfo(id = recipeId))
 }

 LazyColumn {
 item {
 Image(
 painter = rememberImagePainter(recipe.image),
 contentDescription = null,
 modifier = Modifier
 .fillMaxWidth()
 .clip(RoundedCornerShape(25.dp)),
 contentScale = ContentScale.FillWidth
 )

 Spacer(modifier = Modifier.height(16.dp))

 Text(
 text = recipe.summary,
 maxLines = 4,
 overflow = TextOverflow.Ellipsis,
 modifier = Modifier.padding(16.dp)
 )

 RecipeSummary(recipe = recipe)

 Divider()

 Ingredients(recipe = recipe)

 if (recipe.analyzedInstructions.isNotEmpty()) {
 Button(onClick = { /* TODO: Navigate to StepsView */ }) {
 Text("Start Cooking")
 }
 }
 }
 }
}

@Composable
fun Ingredients(recipe: Recipe) {
 Column {
 Text("Ingredients", style = MaterialTheme.typography.h6, modifier = Modifier.padding(8.dp))
 recipe.extendedIngredients.forEach { ingredient ->
 Row(verticalAlignment = Alignment.CenterVertically) {
 Image(
 painter = rememberImagePainter("https://spoonacular.com/cdn/ingredients_100x100/${ingredient.image}"),
 contentDescription = null,
 modifier = Modifier
 .size(50.dp)
 .clip(RoundedCornerShape(15.dp)),
 contentScale = ContentScale.Fit
 )
 Text(ingredient.name.capitalize(), modifier = Modifier.padding(8.dp))
 Spacer(Modifier.weight(1f))
 Text("${ingredient.measures.metric.amount} ${ingredient.measures.metric.unitShort}")
 }
 Divider()
 }
 }
}

@Composable
fun RecipeSummary(recipe: Recipe) {
 Row(modifier = Modifier.fillMaxWidth().padding(8.dp)) {
 Column(modifier = Modifier.weight(1f)) {
 Icon(Icons.Default.Clock, contentDescription = null)
 Text("${recipe.readyInMinutes} Minutes")
 Spacer(Modifier.height(4.dp))
 Icon(Icons.Default.List, contentDescription = null)
 Text("${recipe.extendedIngredients.size} Ingredients")
 }
 Column(modifier = Modifier.weight(1f)) {
 Icon(Icons.Default.ArrowUpward, contentDescription = null)
 Text("${recipe.analyzedInstructions.firstOrNull()?.steps?.size ?: 0} Steps")
 Spacer(Modifier.height(4.dp))
 // Assuming there's a health score icon
 Icon(Icons.Default.ArrowUpward, contentDescription = null)
 Text("${recipe.healthScore} Points")
 }
