 val steps: List<Step> = listOf()
 ) {

 data class Step(
 val equipment: List<Equipment> = listOf(),
 val ingredients: List<Ingredient> = listOf(),
 val length: Length = Length(),
 val number: Int = 0,
 val step: String = ""
 ) {

 data class Equipment(
 val id: Int = 0,
 val image: String = "",
 val localizedName: String = "",
 val name: String = ""
 )


 data class Ingredient(
 val id: Int = 0,
 val image: String = "",
 val localizedName: String = "",
 val name: String = ""
 )


