@Composable
fun HighlightItem(icon: ImageVector, text: String, backgroundColor: Color) {
 Column(horizontalAlignment = Alignment.CenterHorizontally, modifier = Modifier.background(backgroundColor).padding(8.dp).clip(RoundedCornerShape(10.dp))) {
 Icon(icon, contentDescription = null, tint = Color.White, modifier = Modifier.size(24.dp))
 Text(text, style = MaterialTheme.typography.body2.copy(color = Color.White))
 }
}

@Composable
fun DetailHighlight(label: String, value: Any, textColor: Color, isText: Boolean = false) {
 val displayValue = when (value) {
 is Boolean -> if (value) "Yes" else "No"
 else -> value.toString()
 }
 Text("$label: $displayValue", style = MaterialTheme.typography.body2.copy(color = textColor), modifier = Modifier.padding(4.dp))
}


