/**
 * Converts the date time instant to x time ago format
 * x can be minutes, days, hours, weeks, months, years
 */
fun Instant.toTimeAgo(): String {
 val now = Clock.System.now()
 val duration = now - this
 val secondsAgo = duration.inWholeSeconds
 val minutesAgo = secondsAgo / 60
 val hoursAgo = minutesAgo / 60
 val daysAgo = hoursAgo / 24
 val weeksAgo = daysAgo / 7
 val monthsAgo = daysAgo / 30
 val yearsAgo = daysAgo / 365

 return when {
 yearsAgo > 0 -> "$yearsAgo year${if (yearsAgo > 1) "s" else ""} ago"
 monthsAgo > 0 -> "$monthsAgo month${if (monthsAgo > 1) "s" else ""} ago"
 weeksAgo > 0 -> "$weeksAgo week${if (weeksAgo > 1) "s" else ""} ago"
 daysAgo > 0 -> "$daysAgo day${if (daysAgo > 1) "s" else ""} ago"
 hoursAgo > 0 -> "$hoursAgo hour${if (hoursAgo > 1) "s" else ""} ago"
 minutesAgo > 0 -> "$minutesAgo minute${if (minutesAgo > 1) "s" else ""} ago"
 else -> "just now"
 }
}

