 if (!_uiState.value.isLoading) {
 _uiState.update { it.copy(isLoading = true) }
 saveNoteUseCase(text).fold(
 onSuccess = { _uiState.emitSuccess() },
 onFailure = { e -> _uiState.emitError(e) }
 )
 }
 }
 }

