 * Helper function to emit an error state.
 */
 private suspend fun MutableStateFlow<UIState>.emitError(e: Throwable) {
 this.update { currentState ->
 currentState.copy(error = e.message, isLoading = false)
 }
 }

 /**
 * Helper function to emit a success state.
 */
 private suspend fun MutableStateFlow<UIState>.emitSuccess() {
 this.update { currentState ->
 currentState.copy(error = null, isLoading = false)
 }
 }
}

