class MyIrGenerationExtension(private val messageCollector: MessageCollector) : IrGenerationExtension {
 override fun generate(moduleFragment: IrModuleFragment, pluginContext: IrPluginContext) {
 moduleFragment.transform(MyIrTransformer(pluginContext), null)
 }
}

class MyIrTransformer(private val context: IrPluginContext) : IrElementTransformerVoid() {
 override fun visitClass(declaration: IrClass): IrStatement {
 // Modify the class name by appending "JotlinN"
 declaration.name = Name.identifier(declaration.name.identifier + "JotlinN")
 return super.visitClass(declaration)
 }
}
