File Path: /Users/ahmedelshaer/Developer/open/SKIE/SKIE/compiler/kotlin-plugin/src/kgp_common/kotlin/co/touchlab/skie/phases/other/FixDuplicatedOverriddenFunctionsPhase.kt
File Content:
```kt
package co.touchlab.skie.phases.other

import co.touchlab.skie.oir.element.OirSimpleFunction
import co.touchlab.skie.oir.element.copyValueParametersFrom
import co.touchlab.skie.oir.element.memberSimpleFunctions
import co.touchlab.skie.phases.SirPhase
import co.touchlab.skie.sir.element.SirVisibility
import co.touchlab.skie.sir.element.copyValueParametersFrom
import co.touchlab.skie.sir.element.shallowCopy

// Fix for SKIE-395.
// The implementation is somewhat hacky because it creates a fake function that is used to remove the duplicates.
// This fake function is not properly linked with the overrides and as a result this phase needs to run after all other phases that rename functions.
object FixDuplicatedOverriddenFunctionsPhase : SirPhase {

 context(SirPhase.Context)
 override suspend fun execute() {
 oirProvider.allKotlinClassesAndProtocols
 .flatMap { it.memberSimpleFunctions }
 .forEach {
 fixDuplicates(it)
 }
 }

 private fun fixDuplicates(function: OirSimpleFunction) {
 val allVariants = function.allUniqueVariants
 if (allVariants.size <= 1) {
 return
 }

 val mainVariant = allVariants.single { it.selector == function.selector }
 val otherVariants = allVariants - mainVariant

 otherVariants.forEach {
 createInaccessibleOverride(function, it)
 }
 }

 private fun createInaccessibleOverride(function: OirSimpleFunction, baseFunctionToOverride: OirSimpleFunction) {
 val override = OirSimpleFunction(
 selector = baseFunctionToOverride.selector,
 parent = function.parent,
 scope = function.scope,
 returnType = function.returnType,
 errorHandlingStrategy = function.errorHandlingStrategy,
 deprecationLevel = function.deprecationLevel,
 )

 override.copyValueParametersFrom(baseFunctionToOverride)

 override.originalSirFunction = baseFunctionToOverride.originalSirFunction.shallowCopy(
 parent = function.originalSirFunction.parent,
 visibility = SirVisibility.Private,
 ).apply {
 copyValueParametersFrom(baseFunctionToOverride.originalSirFunction)
 }
 }

 private val OirSimpleFunction.allUniqueVariants: List<OirSimpleFunction>
 get() {
 val baseFunctions = overriddenDeclarations.flatMap { it.allUniqueVariants }

 return when (baseFunctions.size) {
 0 -> listOf(this)
 1 -> baseFunctions
 else -> {
 val uniqueSelectors = baseFunctions.map { it.selector }.distinct()

 return uniqueSelectors.map { selector -> baseFunctions.first { it.selector == selector } }
 }
 }
 }
}
```

File Path: /Users/ahmedelshaer/Developer/open/SKIE/SKIE/compiler/kotlin-plugin/src/kgp_common/kotlin/co/touchlab/skie/phases/other/AddAvailabilityBasedDeprecationLevelPhase.kt
File Content:
```kt
package co.touchlab.skie.phases.other

import co.touchlab.skie.kir.element.DeprecationLevel
import co.touchlab.skie.phases.SirPhase
import co.touchlab.skie.sir.element.SirCallableDeclaration
import co.touchlab.skie.sir.element.SirElementWithFunctionBodyBuilder
import co.touchlab.skie.sir.element.SirFunction
import co.touchlab.skie.sir.element.SirProperty
import co.touchlab.skie.util.swift.quoteAsSwiftLiteral

object AddAvailabilityBasedDeprecationLevelPhase : SirPhase {

 context(SirPhase.Context)
 override suspend fun execute() {
 sirProvider.allSkieGeneratedCallableDeclarations.forEach {
 it.applyDeprecationLevel()
 }
 }

 private fun SirCallableDeclaration.applyDeprecationLevel() {
 when (val deprecationLevel = this.deprecationLevel) {
 is DeprecationLevel.Error -> {
 this.addAvailableAttribute("unavailable", deprecationLevel.message)

 this.replaceBodyWithError()
 }
 is DeprecationLevel.Warning -> this.addAvailableAttribute("deprecated", deprecationLevel.message)
 DeprecationLevel.None -> {}
 }
 }

 private fun SirCallableDeclaration.addAvailableAttribute(type: String, message: String?) {
 val messageParameter = if (message != null) ", message: ${message.quoteAsSwiftLiteral()}" else ""

 this.attributes.add("available(*, $type$messageParameter)")
 }

 private fun SirCallableDeclaration.replaceBodyWithError() {
 when (this) {
 is SirFunction -> (this as SirElementWithFunctionBodyBuilder).replaceBodyWithError()
 is SirProperty -> {
 this.getter?.replaceBodyWithError()
 this.setter?.replaceBodyWithError()
 }
 }
 }

 private fun SirElementWithFunctionBodyBuilder.replaceBodyWithError() {
 this.bodyBuilder.clear()
 this.bodyBuilder.add {
 addStatement("fatalError(\"Unavailable\")")
 }
 }
}
```

File Path: /Users/ahmedelshaer/Developer/open/SKIE/SKIE/compiler/kotlin-plugin/src/kgp_common/kotlin/co/touchlab/skie/phases/swift/CompileSwiftPhase.kt
File Content:
```kt
package co.touchlab.skie.phases.swift

import co.touchlab.skie.configuration.SkieConfigurationFlag
import co.touchlab.skie.phases.SirPhase
import co.touchlab.skie.sir.element.SirCompilableFile
import co.touchlab.skie.util.Command
import org.jetbrains.kotlin.backend.konan.BitcodeEmbedding
import org.jetbrains.kotlin.backend.konan.KonanConfigKeys
import org.jetbrains.kotlin.konan.target.platformName
import org.jetbrains.kotlin.konan.target.withOSVersion
import java.io.File

class CompileSwiftPhase(
 context: SirPhase.Context,
) : SirPhase {

 private val framework = context.framework
 private val cacheableKotlinFramework = context.cacheableKotlinFramework
 private val konanConfig = context.konanConfig
 private val swiftCompilerConfiguration = context.swiftCompilerConfiguration
 private val skieConfiguration = context.skieConfiguration
 private val configurables = context.configurables
 private val skieBuildDirectory = context.skieBuildDirectory
 private val targetTriple = context.configurables.targetTriple
 private val swiftFrameworkHeader = context.skieBuildDirectory.swiftCompiler.moduleHeader(framework.moduleName)
 private val swiftFileList = context.skieBuildDirectory.swiftCompiler.config.swiftFileList(framework.moduleName)
 private val outputFileMap = context.skieBuildDirectory.swiftCompiler.config.outputFileMap
 private val objectFiles = skieBuildDirectory.swiftCompiler.objectFiles
 private val moduleDirectory = skieBuildDirectory.swiftCompiler.module

 private val isDebug = konanConfig.debug
 private val isLibraryEvolutionEnabled = SkieConfigurationFlag.Build_SwiftLibraryEvolution in skieConfiguration.enabledConfigurationFlags
 private val isParallelSwiftCompilationEnabled = SkieConfigurationFlag.Build_ParallelSwiftCompilation in skieConfiguration.enabledConfigurationFlags
 private val isConcurrentSkieCompilationEnabled = SkieConfigurationFlag.Build_ConcurrentSkieCompilation in skieConfiguration.enabledConfigurationFlags

 context(SirPhase.Context)
 override suspend fun execute() {
 val sourceFiles = sirProvider.skieModuleFiles.filterIsInstance<SirCompilableFile>().map { it.absolutePath.toFile() }
 if (sourceFiles.isEmpty()) {
 return
 }

 createSwiftFileList(sourceFiles)

 createOutputFileMap(sourceFiles)

 callSwiftCompiler()

 deleteOldObjectFiles(sourceFiles)

 copySwiftModuleFiles()

 copySwiftLibraryEvolutionFiles()

 addSwiftSpecificLinkerArgs()
 }

 private fun createSwiftFileList(sourceFiles: List<File>) {
 val content = sourceFiles.joinToString("\n") { "'${it.absolutePath}'" }

 swiftFileList.writeText(content)
 }

 private fun createOutputFileMap(sourceFiles: List<File>) {
 if (!isDebug) {
 return
 }

 val root = """
 "": {
 "emit-module-dependencies": "${moduleDirectory.dependencies(framework.moduleName).absolutePath}",
 "swift-dependencies": "${moduleDirectory.swiftDependencies(framework.moduleName).absolutePath}"
 },
 """.trimIndent()

 val body = sourceFiles.joinToString(",\n") { sourceFile ->
 val sourceFileName = sourceFile.nameWithoutExtension

 """
 "${sourceFile.absolutePath}": {
 "object": "${objectFiles.objectFile(sourceFileName).absolutePath}",
 "dependencies": "${objectFiles.dependencies(sourceFileName).absolutePath}",
 "swift-dependencies": "${objectFiles.swiftDependencies(sourceFileName).absolutePath}",
 "swiftmodule": "${objectFiles.partialSwiftModule(sourceFileName).absolutePath}"
 }
 """.trimIndent()
 }

 val content = "{\n$root\n$body\n}"

 outputFileMap.writeText(content)
 }

 private fun callSwiftCompiler() {
 Command("${configurables.absoluteTargetToolchain}/usr/bin/swiftc").apply {
 +listOf("-module-name", framework.moduleName)
 +"-import-underlying-module"
 +"-F"
 +cacheableKotlinFramework.parentDir.absolutePath
 +"-F"
 +skieBuildDirectory.swiftCompiler.fakeObjCFrameworks.directory.absolutePath
 +"-verify-emitted-module-interface"
 +"-emit-module"
 +"-emit-module-path"
 +swiftFrameworkHeader.swiftModule
 if (isLibraryEvolutionEnabled) {
 +"-enable-library-evolution"
 +"-emit-module-interface-path"
 +swiftFrameworkHeader.swiftInterface
 +"-emit-private-module-interface-path"
 +swiftFrameworkHeader.privateSwiftInterface
 }
 +"-emit-objc-header"
 +"-emit-objc-header-path"
 +swiftFrameworkHeader.swiftHeader
 getSwiftcBitcodeArg()?.let { +it }
 +"-emit-object"
 +"-parse-as-library"
 +"-enable-batch-mode"
 if (isDebug) {
 +"-Onone"
 +"-incremental"
 +"-output-file-map"
 +outputFileMap.absolutePath
 } else {
 +"-O"
 +"-whole-module-optimization"
 }
 +"-g"
 +"-module-cache-path"
 +skieBuildDirectory.cache.swiftModules.directory.absolutePath
 +"-swift-version"
 +swiftCompilerConfiguration.swiftVersion
 +parallelizationArgument
 +"-sdk"
 +configurables.absoluteTargetSysRoot
 +"-target"
 +configurables.targetTriple.withOSVersion(configurables.osVersionMin).toString()
 +"@${swiftFileList.absolutePath}"

 workingDirectory = objectFiles.directory

 execute(logFile = skieBuildDirectory.debug.logs.swiftc)
 }
 }

 private fun deleteOldObjectFiles(sourceFiles: List<File>) {
 if (isDebug) {
 val sourceFilesNames = sourceFiles.map { it.nameWithoutExtension }.toSet()

 objectFiles.allFiles
 .filterNot { it.nameWithoutExtension in sourceFilesNames }
 .forEach {
 it.delete()
 }
 } else {
 objectFiles.allFiles
 .filter { it.nameWithoutExtension != framework.moduleName }
 .forEach {
 it.delete()
 }
 }
 }

 private fun getSwiftcBitcodeArg() =
 when (konanConfig.configuration.get(KonanConfigKeys.BITCODE_EMBEDDING_MODE)) {
 BitcodeEmbedding.Mode.NONE, null -> null
 BitcodeEmbedding.Mode.FULL -> "-embed-bitcode"
 BitcodeEmbedding.Mode.MARKER -> "-embed-bitcode-marker"
 }

 private fun copySwiftModuleFiles() {
 val copyFiles = mapOf(
 swiftFrameworkHeader.swiftModule to framework.swiftModule(targetTriple),
 swiftFrameworkHeader.swiftDoc to framework.swiftDoc(targetTriple),
 swiftFrameworkHeader.abiJson to framework.abiJson(targetTriple),
 swiftFrameworkHeader.swiftSourceInfo to framework.swiftSourceInfo(targetTriple),
 swiftFrameworkHeader.swiftHeader to framework.swiftHeader,
 )

 copyFiles.forEach { (source, target) ->
 source.copyTo(target, overwrite = true)
 }
 }

 private fun copySwiftLibraryEvolutionFiles() {
 if (isLibraryEvolutionEnabled) {
 swiftFrameworkHeader.swiftInterface.copyTo(framework.swiftInterface(targetTriple), overwrite = true)
 swiftFrameworkHeader.privateSwiftInterface.copyTo(framework.privateSwiftInterface(targetTriple), overwrite = true)
 } else {
 framework.swiftInterface(targetTriple).delete()
 framework.privateSwiftInterface(targetTriple).delete()
 }
 }

 private fun addSwiftSpecificLinkerArgs() {
 val swiftLibSearchPaths = listOf(
 File(configurables.absoluteTargetToolchain, "usr/lib/swift/${configurables.platformName().lowercase()}"),
 File(configurables.absoluteTargetSysRoot, "usr/lib/swift"),
 ).flatMap { listOf("-L", it.absolutePath) }

 val otherLinkerFlags = listOf(
 "-rpath", "/usr/lib/swift", "-dead_strip",
 )

 konanConfig.configuration.addAll(KonanConfigKeys.LINKER_ARGS, swiftLibSearchPaths)
 konanConfig.configuration.addAll(KonanConfigKeys.LINKER_ARGS, otherLinkerFlags)
 }

 private val parallelizationArgument: String
 get() {
 val numberOfAvailableProcessors = if (isParallelSwiftCompilationEnabled) {
 if (isConcurrentSkieCompilationEnabled) {
 (Runtime.getRuntime().availableProcessors() - 1).coerceAtLeast(1)
 } else {
 Runtime.getRuntime().availableProcessors()
 }
 } else {
 1
 }

 return "-j$numberOfAvailableProcessors"
 }
}
```

