```kt
package co.touchlab.skie.kir.irbuilder.impl.template

import co.touchlab.skie.kir.descriptor.MutableDescriptorProvider
import co.touchlab.skie.kir.irbuilder.FunctionBuilder
import co.touchlab.skie.kir.irbuilder.Namespace
import co.touchlab.skie.kir.irbuilder.impl.symboltable.DummyIrSimpleFunction
import co.touchlab.skie.kir.irbuilder.impl.symboltable.IrRebindableSimpleFunctionPublicSymbol
import co.touchlab.skie.phases.KotlinIrPhase
import co.touchlab.skie.phases.SymbolTablePhase
import org.jetbrains.kotlin.backend.common.lower.DeclarationIrBuilder
import org.jetbrains.kotlin.descriptors.CallableMemberDescriptor
import org.jetbrains.kotlin.descriptors.FunctionDescriptor
import org.jetbrains.kotlin.descriptors.annotations.Annotations
import org.jetbrains.kotlin.descriptors.impl.SimpleFunctionDescriptorImpl
import org.jetbrains.kotlin.ir.declarations.IrSimpleFunction
import org.jetbrains.kotlin.ir.expressions.IrBody
import org.jetbrains.kotlin.ir.symbols.IrSimpleFunctionSymbol
import org.jetbrains.kotlin.name.Name

class FunctionTemplate(
 name: Name,
 namespace: Namespace<*>,
 annotations: Annotations,
 config: FunctionBuilder.() -> Unit,
) : BaseDeclarationTemplate<FunctionDescriptor, IrSimpleFunction, IrSimpleFunctionSymbol>() {

 override val descriptor: SimpleFunctionDescriptorImpl = SimpleFunctionDescriptorImpl.create(
 namespace.descriptor,
 annotations,
 name,
 CallableMemberDescriptor.Kind.SYNTHESIZED,
 namespace.sourceElement,
 )

 private val functionBuilder = FunctionBuilder(descriptor)

 // TODO Change to context(KotlinIrPhase.Context, DeclarationIrBuilder) once are context implemented properly
 private val irBodyBuilder: context(KotlinIrPhase.Context) DeclarationIrBuilder.(IrSimpleFunction) -> IrBody

 init {
 functionBuilder.config()

 irBodyBuilder = functionBuilder.body ?: throw IllegalStateException("Function must have a body.")

 descriptor.initialize(
 functionBuilder.extensionReceiverParameter,
 functionBuilder.dispatchReceiverParameter,
 functionBuilder.contextReceiverParameters,
 functionBuilder.typeParameters,
 functionBuilder.valueParameters,
 functionBuilder.returnType,
 functionBuilder.modality,
 functionBuilder.visibility,
 )

 descriptor.isInline = functionBuilder.isInline
 descriptor.isSuspend = functionBuilder.isSuspend
 }

 context(MutableDescriptorProvider)
 override fun registerExposedDescriptor() {
 this@MutableDescriptorProvider.exposeCallableMember(descriptor)
 }

 context(SymbolTablePhase.Context)
 override fun declareSymbol() {
 val signature = skieSymbolTable.kotlinSymbolTable.signaturer.composeSignature(descriptor)
 ?: throw IllegalArgumentException("Only exported declarations are currently supported. Check declaration visibility.")

 // IrRebindableSimpleFunctionPublicSymbol is used so that we can later bind it to the correct declaration which cannot be created before the symbol table is validated to not contain any unbound symbols.
 val symbolFactory = { IrRebindableSimpleFunctionPublicSymbol(signature, descriptor) }
 val functionFactory = { symbol: IrSimpleFunctionSymbol ->
 DummyIrSimpleFunction(symbol).also {
 // In 1.8.0 the symbol is already present before calling declareSimpleFunction and therefore is not IrRebindableSimpleFunctionPublicSymbol
 // Starting from 1.9.0 the SymbolTable has additional check that requires that the symbol of created function is bounded in the factory.
 if (symbol is IrRebindableSimpleFunctionPublicSymbol) {
 symbol.bind(it)
 }
 }
 }

 val declaration = skieSymbolTable.kotlinSymbolTable.declareSimpleFunction(signature, symbolFactory, functionFactory)
 // But the symbol cannot be bounded otherwise DeclarationBuilder will not to generate the declaration (because it thinks it already exists).
 (declaration.symbol as? IrRebindableSimpleFunctionPublicSymbol)?.unbind()
 }

 context(KotlinIrPhase.Context)
 override fun getSymbol(): IrSimpleFunctionSymbol =
 skieSymbolTable.descriptorExtension.referenceSimpleFunction(descriptor)

 context(KotlinIrPhase.Context)
 override fun initializeBody(declaration: IrSimpleFunction, declarationIrBuilder: DeclarationIrBuilder) {
 declaration.body = irBodyBuilder(this@Context, declarationIrBuilder, declaration)
 }
}
```

File Path: /Users/ahmedelshaer/Developer/open/SKIE/SKIE/compiler/kotlin-plugin/src/kgp_common/kotlin/co/touchlab/skie/kir/irbuilder/impl/template/BaseDeclarationTemplate.kt
File Content:
```kt
@file:Suppress("invisible_reference", "invisible_member")

package co.touchlab.skie.kir.irbuilder.impl.template

import co.touchlab.skie.kir.irbuilder.DeclarationTemplate
import co.touchlab.skie.phases.KotlinIrPhase
import org.jetbrains.kotlin.backend.common.lower.DeclarationIrBuilder
import org.jetbrains.kotlin.descriptors.DeclarationDescriptor
import org.jetbrains.kotlin.ir.declarations.IrDeclaration
import org.jetbrains.kotlin.ir.declarations.IrDeclarationContainer
import org.jetbrains.kotlin.ir.symbols.IrBindableSymbol
import org.jetbrains.kotlin.ir.util.patchDeclarationParents
import org.jetbrains.kotlin.psi2ir.generators.GeneratorContext
import org.jetbrains.kotlin.psi2ir.generators.SyntheticDeclarationsGenerator

abstract class BaseDeclarationTemplate<D : DeclarationDescriptor, IR : IrDeclaration, S : IrBindableSymbol<D, IR>> :
 DeclarationTemplate<D> {

 context(KotlinIrPhase.Context)
 override fun generateIrDeclaration(parent: IrDeclarationContainer, generatorContext: GeneratorContext) {
 createDeclarationStubsIfIrLazyClass(parent)

 val syntheticDeclarationsGenerator = SyntheticDeclarationsGenerator(generatorContext)

 descriptor.accept(syntheticDeclarationsGenerator, parent)

 val symbol = getSymbol()
 val ir = symbol.owner

 ir.patchDeclarationParents(ir.parent)
 }

 private fun createDeclarationStubsIfIrLazyClass(parent: IrDeclarationContainer) {
 // Instantiates lazy value
 parent.declarations
 }

 context(KotlinIrPhase.Context)
 override fun generateIrBody() {
 val symbol = getSymbol()

 val declarationIrBuilder = DeclarationIrBuilder(pluginContext, symbol, startOffset = 0, endOffset = 0)

 initializeBody(symbol.owner, declarationIrBuilder)
 }

 context(KotlinIrPhase.Context)
 protected abstract fun getSymbol(): S

 context(KotlinIrPhase.Context)
 protected abstract fun initializeBody(declaration: IR, declarationIrBuilder: DeclarationIrBuilder)
}
```

File Path: /Users/ahmedelshaer/Developer/open/SKIE/SKIE/compiler/kotlin-plugin/src/kgp_common/kotlin/co/touchlab/skie/kir/irbuilder/impl/template/SecondaryConstructorTemplate.kt
File Content:
```kt
package co.touchlab.skie.kir.irbuilder.impl.template

import co.touchlab.skie.compilerinject.reflection.reflectedBy
import co.touchlab.skie.compilerinject.reflection.reflectors.DeclarationDescriptorImplReflector
import co.touchlab.skie.kir.descriptor.MutableDescriptorProvider
import co.touchlab.skie.kir.irbuilder.Namespace
import co.touchlab.skie.kir.irbuilder.SecondaryConstructorBuilder
import co.touchlab.skie.kir.irbuilder.impl.symboltable.DummyIrConstructor
import co.touchlab.skie.kir.irbuilder.impl.symboltable.IrRebindableConstructorPublicSymbol
import co.touchlab.skie.phases.KotlinIrPhase
import co.touchlab.skie.phases.SymbolTablePhase
import org.jetbrains.kotlin.backend.common.lower.DeclarationIrBuilder
import org.jetbrains.kotlin.descriptors.ClassConstructorDescriptor
import org.jetbrains.kotlin.descriptors.ClassDescriptor
import org.jetbrains.kotlin.descriptors.annotations.Annotations
import org.jetbrains.kotlin.descriptors.impl.ClassConstructorDescriptorImpl
import org.jetbrains.kotlin.ir.declarations.IrConstructor
import org.jetbrains.kotlin.ir.expressions.IrBody
import org.jetbrains.kotlin.ir.symbols.IrConstructorSymbol
import org.jetbrains.kotlin.name.Name

class SecondaryConstructorTemplate(
 name: Name,
 namespace: Namespace<ClassDescriptor>,
 annotations: Annotations,
 config: SecondaryConstructorBuilder.() -> Unit,
) : BaseDeclarationTemplate<ClassConstructorDescriptor, IrConstructor, IrConstructorSymbol>() {

 override val descriptor: ClassConstructorDescriptorImpl = ClassConstructorDescriptorImpl.create(
 namespace.descriptor,
 annotations,
 false,
 namespace.sourceElement,
 )

 private val constructorBuilder = SecondaryConstructorBuilder(descriptor)

 // TODO Change to context(KotlinIrPhase.Context, DeclarationIrBuilder) once are context implemented properly
 private val irBodyBuilder: context(KotlinIrPhase.Context) DeclarationIrBuilder.(IrConstructor) -> IrBody

 init {
 descriptor.reflectedBy<DeclarationDescriptorImplReflector>().name = name

 constructorBuilder.config()

 irBodyBuilder = constructorBuilder.body ?: throw IllegalStateException("Constructor must have a body.")

 descriptor.initialize(
 constructorBuilder.valueParameters,
 constructorBuilder.visibility,
 )

 descriptor.returnType = namespace.descriptor.defaultType
 }

 context(MutableDescriptorProvider)
 override fun registerExposedDescriptor() {
 this@MutableDescriptorProvider.exposeCallableMember(descriptor)
 }

 context(SymbolTablePhase.Context)
 override fun declareSymbol() {
 val signature = skieSymbolTable.kotlinSymbolTable.signaturer.composeSignature(descriptor)
 ?: throw IllegalArgumentException("Only exported declarations are currently supported. Check declaration visibility.")

 // IrRebindableConstructorPublicSymbol is used so that we can later bind it to the correct declaration which cannot be created before the symbol table is validated to not contain any unbound symbols.
 val symbolFactory = { IrRebindableConstructorPublicSymbol(signature, descriptor) }
 val functionFactory = { symbol: IrConstructorSymbol ->
 DummyIrConstructor(symbol).also {
 // In 1.8.0 the symbol is already present before calling declareConstructor and therefore is not IrRebindableConstructorPublicSymbol
 // Starting from 1.9.0 the SymbolTable has additional check that requires that the symbol of created function is bounded in the factory.
 if (symbol is IrRebindableConstructorPublicSymbol) {
 symbol.bind(it)
 }
 }
 }

 val declaration = skieSymbolTable.kotlinSymbolTable.declareConstructor(signature, symbolFactory, functionFactory)
 // But the symbol cannot be bounded otherwise DeclarationBuilder will not to generate the declaration (because it thinks it already exists).
 (declaration.symbol as? IrRebindableConstructorPublicSymbol)?.unbind()
 }

 context(KotlinIrPhase.Context)
 override fun getSymbol(): IrConstructorSymbol =
 skieSymbolTable.descriptorExtension.referenceConstructor(descriptor)

 context(KotlinIrPhase.Context)
 override fun initializeBody(declaration: IrConstructor, declarationIrBuilder: DeclarationIrBuilder) {
 declaration.body = irBodyBuilder(this@Context, declarationIrBuilder, declaration)
 }
}
```

