@Composable
fun RecipeInfo(recipeId: Int, store: RecipeInfoStore = koinInject()) {
 val recipe by remember { store.observeState() }.collectAsState(RecipeInfoState.Initial)

 LaunchedEffect(Unit) {
 store.dispatch(RecipeInfoAction.GetRecipeInfo(id = recipeId))
 }

 LazyColumn(modifier = Modifier.padding(16.dp)) {
 item {
 KamelImage(
 resource = asyncPainterResource(recipe.recipe.image),
 contentDescription = null,
 modifier = Modifier
 .fillMaxWidth()
 .clip(RoundedCornerShape(25.dp)),
 contentScale = ContentScale.Crop
 )
 Spacer(modifier = Modifier.height(16.dp))

 Text(
 text = recipe.recipe.summary,
 maxLines = 4,
 overflow = TextOverflow.Ellipsis,
 modifier = Modifier.padding(16.dp)
 )

 RecipeSummary(recipe = recipe.recipe)

 Divider()

 Ingredients(recipe = recipe.recipe)

 if (recipe.recipe.analyzedInstructions.isNotEmpty()) {
 Button(onClick = { /* TODO: Navigate to StepsView */ }) {
 Text("Start Cooking")
 }
 }
 }
 }
}

