@Composable

fun RecipeCard(

 recipe: SearchRecipe,

 onRecipeClicked: (recipeId: Int) -> Unit

) {

 var isFavorite by remember { mutableStateOf(false) }

 Card(

 modifier = Modifier

 .padding(8.dp)

 .background(

 brush = Brush.verticalGradient(

 colors = listOf(Color.LightGray, MaterialTheme.colorScheme.surface)

 )

 )

 .clickable { onRecipeClicked(recipe.id) },

 shape = RoundedCornerShape(10.dp)

 ) {

 Column(modifier = Modifier.padding(8.dp)) {

 KamelImage(

 resource = asyncPainterResource(recipe.image),

 contentDescription = recipe.title,

 modifier = Modifier

 .height(180.dp)

 .fillMaxWidth()

 .clip(RoundedCornerShape(10.dp)),

 contentScale = ContentScale.Crop

 )

 Spacer(Modifier.height(16.dp))

 Row(verticalAlignment = Alignment.CenterVertically) {

 Text(

 text = recipe.title,

 style = MaterialTheme.typography.headlineSmall.copy(fontWeight = FontWeight.Bold),

 modifier = Modifier.weight(1f),

 color = MaterialTheme.colorScheme.onSurface

 )

 IconButton(onClick = { isFavorite = !isFavorite }) {

 Icon(

 imageVector = if (isFavorite) Icons.Filled.Favorite else Icons.Filled.FavoriteBorder,

 contentDescription = "Favorite",

 tint = if (isFavorite) Color.Red else Color.Gray

 )

 }

 }

 IngredientStats(recipe)

 }

 }

}



@Composable

private fun IngredientStats(recipe: SearchRecipe) {

 Row(verticalAlignment = Alignment.CenterVertically) {

 Icon(

 imageVector = Icons.Default.ThumbUp, // Use appropriate icon

 contentDescription = "Likes",

 tint = MaterialTheme.colorScheme.primary

 )

 Text(

 text = " ${recipe.likes}",

 style = MaterialTheme.typography.bodyMedium,

 color = MaterialTheme.colorScheme.onSurface

 )

 Spacer(modifier = Modifier.width(8.dp))

 Icon(

 imageVector = Icons.Default.Info, // Use appropriate icon

 contentDescription = "Ingredients",

 tint = MaterialTheme.colorScheme.primary

 )

 Text(

 text = " Used: ${recipe.usedIngredients.size}, Missed: ${recipe.missedIngredients.size}",

 style = MaterialTheme.typography.bodyMedium,

 color = MaterialTheme.colorScheme.onSurface

 )

 }

